
public class Task4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 15, b = 35;
		System.out.println("Before swap operation a = " + a + " and b = " + b);
		a = a + b;
		b = a - b;
		a = a - b;
		System.out.println("After swap operation a = " + a + " and b = " + b);
		
	}

}
